        tk.Label(marco_principal, text="Clave de Recuperaci贸n (Opcional):", font=FUENTE_PRINCIPAL, bg=COLOR_FONDO, fg=COLOR_FRENTE).pack(anchor="w", pady=(10, 0))
        
        # Contenedor para la entrada y el bot贸n de "ver"
