        boton_cifrar = tk.Button(marco_botones, text="Cifrar", command=self.cifrar_archivo, 
                                 bg=COLOR_BOTON, fg=COLOR_TEXTO_BOTON, font=FUENTE_PRINCIPAL, relief="flat", width=15, pady=5)
